import pandas as pdimport logging as logimport pathlib as pldef read_data_from_csv(path) -> pd.DataFrame:    """    Read the data from the csv file    Parameters:        path (pathlib.Path): Path to the csv file    Return:         pd.DataFrame: Dataframe of the data    """    return pd.read_csv(path)def process_weather_info_to_data_structures(data) -> tuple[list, list, list, list, list, dict, list, dict, list, list]:    """    Process the weather info into data structures    Parameters:        data (pd.DataFrame): Dataframe of the weather info    Return:        tuple[list, list, list, list, dict, list, list]: Processed data (country, city, date, time, condition,        temperature, humidity, wind_speed, latitude, longitude)    """    # Split the weather info into separate columns    country = data["country"].tolist()    city = data["location_name"].tolist()    # Split the date and time    date, time = zip(*[date_time.split(" ") for date_time in data["last_updated"].tolist()])    date = list(date)    time = list(time)    # Get the weather condition    condition = data["condition_text"].tolist()    # Get the temperature and place into a dictionary of fahrenheit and celsius    temperature = {"fahrenheit": data["temperature_fahrenheit"].tolist(),                   "celsius": data["temperature_celsius"].tolist()}    # Get the humidity    humidity = data["humidity"].tolist()    # Get the wind speed    wind_speed = {"mph": data["wind_mph"].tolist(),                  "kph": data["wind_kph"].tolist()}    # Get the latitude and longitude    latitude = data["latitude"].tolist()    longitude = data["longitude"].tolist()    # Tupled data for return    processed_data = country, city, date, time, condition, temperature, humidity, wind_speed, latitude, longitude    # Return the processed data    return processed_datadef main():    """    Main function to run the program    """    # Hard coded relative path to the data file    # Data set from https://www.kaggle.com/datasets/nelgiriyewithana/global-weather-repository    file_path = pl.Path("resources/GlobalWeatherRepository.csv")    # Read the data    data = read_data_from_csv(file_path)    log.info("    Data read successfully\n")    log.debug(f"\n{data.head(1)}\n")    # Process the weather info into data structures    data = process_weather_info_to_data_structures(data)    log.info("    Data preprocessed successfully\n")    log.debug(f"\n{[first_of_set[0] if isinstance(first_of_set, list) else first_of_set.keys()                    for first_of_set in data]}\n")if __name__ == "__main__":    log.basicConfig(level=log.DEBUG)    main()