import osfrom PyQt5.QtWidgets import QApplication, QFileDialogimport logging as logfrom pandas import DataFrame, read_csvfrom pathlib import Pathdef retrieve_file() -> str:    """    Open a file dialog to select the data file to process    Return:        str: Path to the selected file    """    # Create an application instance    app = QApplication([])    # Get the user's home directory    default_dir = "./resources"    if not os.path.exists(default_dir):        default_dir = os.path.expanduser("~")    # Open a file dialog to select the data file    file_path, _ = QFileDialog.getOpenFileName(        None, "Select a CSV file", default_dir, "CSV files (*.csv);;All files (*)"    )    return file_pathdef read_data_from_csv(path: str | Path) -> DataFrame:    """    Read the data from the csv file    Parameters:        path (str | Path): Path to the csv file    Return:         DataFrame: Dataframe of the data    """    if isinstance(path, Path):        if not path.exists():            raise FileNotFoundError(f"File not found: {path}")    else:        if not Path(path).exists():            raise FileNotFoundError(f"File not found: {path}")    try:        return_data = read_csv(path)    except Exception as e:        raise Exception(f"Error reading file: {e}")    return return_datadef main():    """    Main function to run the program, opens a file dialog to select the data file to process    """    # Open a os file dialog to select the data file    file_path = retrieve_file()    # Read the data    data = read_data_from_csv(file_path)    log.info("\n\tData read successfully\n")    log.debug(f"\n{data.head(5)}\n")if __name__ == "__main__":    # Set the log level to debug    log.basicConfig(level=log.DEBUG)    main()